var FormDropzone = function() {
    return {
        init: function() {
            Dropzone.options.myDropzone = {
                dictDefaultMessage: "",
                // thumbnailWidth: null,
                // thumbnailHeight: null,
                acceptedFiles: 'image/*',

                init: function() {

                    this.on("thumbnail", function(file, dataUrl) {
                        $('.dz-image').find('img').attr({width: '100%', height: '100%'});
                    }),
                    this.on("success", function(file) {
                        $('.dz-image').css({"width":"100%", "height":"auto"});
                    })

                    this.on("addedfile", function(e) {
                        var n = Dropzone.createElement("<a href='javascript:;'' class='btn red btn-sm btn-block'>Remove</a>"),
                            t = this;
                        n.addEventListener("click", function(n) {
                            n.preventDefault(), n.stopPropagation(), t.removeFile(e)
                        }), e.previewElement.appendChild(n)
                    })

                    this.on('queuecomplete', function() {

                        // alert('hi');
                        var imagesArr = $('.dz-image').children('img').map(function(){
                            return $(this).attr('src')
                        }).get();

                        var imagesStr = imagesArr.join("@");


                        // localStorage.setItem('images', imagesStr);
                        //
                        // var cc = localStorage.getItem('images');
                        //
                        // console.log(cc);
                        //
                        // $('#imagesStr').val(imagesStr)
                    });
                }
            }
        }
    }
}();
jQuery(document).ready(function() {
    FormDropzone.init()
});